// import { getProducts } from "../../service/index";

const INITIAL_STATE = {
  products: [],
};

function productsReducer(state = INITIAL_STATE, action) {
  // La fonction qui est le Reducer
  switch (action.type) {
    case "LOADPRODUCTS": {
      return {
        ...state,
        products: action.payload,
      };
    }
  }

  return state;
}
export default productsReducer;

