import axios from "axios";

export const fetchProduct = () => {
  return new Promise((onSuccess, onFail) => {
    axios
    .get("api/products")
    .then((response, error) => {
      if (!response || error) {
        return onFail(`Response failure : ${error}`);
      }
      return onSuccess(response)
    })


  });

};
