import axios from "axios";

const getProducts = () => {
  return new Promise((onSuccess, onFail) => {
    axios
    .get("loapi/products")
    .then((response, error) => {
      if (!response || error) {
        return onFail(`Response failure : ${error}`);
      }
      onSuccess(response);
      console.log(onSuccess)
    });

  });

};

export default getProducts
