import axios from "axios";


export const getProducts = () => {

  return new Promise((onSuccess, onFail) => {
    axios.get(REACT_APP_PRODUCTS).then((response, error) => {
      if (!response || error) {
        return onFail(`Response failure : ${error}`);
      }
      return onSuccess(response);
    });
  });
};
